sys_script_client.script:
function onLoad() {
	var releaseName = localStorage.getItem("key1");
	//releaseName=releaseName.toLowerCase();
	//alert(releaseName);
    // Define the release name you want to search for
    //var releaseName = 'YourReleaseName'; // Replace 'YourReleaseName' with the actual release name

    // Make an AJAX call to the server-side script include
    var ga = new GlideAjax('ReleaseSysIdUtil');
    ga.addParam('sysparm_name', 'getSysIdByReleaseName');
    ga.addParam('sysparm_releaseName', releaseName);
    ga.getXMLAnswer(function(response) {
        var releaseSysId = response;
        if (releaseSysId) {
            // Sys_id is returned, do something with it
            console.log('Sys ID of release ' + releaseName + ': ' + releaseSysId);

            // Example: Set the sys_id in a field on the form
            g_form.setValue('u_release_sys_id', releaseSysId); // Assuming the field name is 'u_release_sys_id'
        } else {
            // Sys_id is not returned or is empty, handle error or no data scenario
            console.log('Release ' + releaseName + ' not found');
        }
    });
}
